import React, { Component, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form'
import Alert from 'react-bootstrap/Alert';

function TaskItem(props) {
  const [show, setShow] = useState(false);
  const [title, setTitle] = useState(props.task.title); 
  const [comments, setComments] = useState(props.task.body); 

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const onClose = () => {
    setTitle(props.task.title);
    setComments(props.task.body);
    handleClose();
  }

  const onSubmit = () => {
    //// Check Input ////
    if(!title.trim().length && !comments.trim().length){ // invalid title & comments
      setTitle(props.task.title);
      setComments(props.task.body);
    }else if(!title.trim().length){ // invalid title
      setTitle(props.task.title);
    }else if(!comments.trim().length){ // invalid comments
      setComments(props.task.body);
    }else{ //// OK, Do Edit ////
      handleClose();
    }
  }

  return (
    <div>
      <div class="d-flex justify-content-between mx-5 my-3 px-5">     
        <div class="d-flex flex-column mx-5"> 
          <h5 class="fw-bold text-start">
            {props.task.title}
            <span class="badge rounded-pill bg-light text-dark mx-3"> {props.task.state} </span>
          </h5>
          <p class="text-start">{props.task.body}</p>
        </div>
        <div class="d-flex align-items-center mx-5">
          <button 
          type='button'
          class=" mx-1 btn btn-outline-info btn-sm"
          data-bs-toggle="modal" data-bs-target="#exampleMod"
          onClick={handleShow}
          >
            Edit
          </button>
          <button 
          class=" mx-1 btn btn-outline-danger btn-sm"
          onClick={() => {}}
          >
            Delete
          </button>
        </div>
      </div>    

      <Modal show={show} onHide={handleClose} aria-labelledby="contained-modal-title-vcenter" centered>
        <Modal.Header closeButton>
          <Modal.Title>Edit Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="input"
                placeholder="Enter New Title"
                value={title}
                onChange={(e) => {setTitle(e.target.value)}}
              />
            </Form.Group>
            <Form.Group
              className="mb-3"
            >
              <Form.Label>Comments</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={5} 
                placeholder="Enter New Comments"
                value={comments}
                onChange={(e) => {setComments(e.target.value)}}
                />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onClose}>
            Close
          </Button>
          <Button variant="primary" onClick={onSubmit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      
    </div>
  )
}

export default TaskItem
