import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form'

function TaskItem(props) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [value, setValue] = useState({title: "", comment: ""});
  const onValue = (e) => {
    const {title, comment} = e.target;
    setValue.title = title;
    setValue.comment = comment;
  }
        onFormSubmit = e => {
          e.preventDefault()
          console.log(value)
          setValue()
        }


  return (
    <div>
      <div class="d-flex justify-content-between mx-5 my-3 px-5">     
        <div class="d-flex flex-column mx-5"> 
          <h5 class="fw-bold text-start">
            {props.task.title}
            <span class="badge rounded-pill bg-light text-dark mx-3"> {props.task.state} </span>
          </h5>
          <p class="text-start">{props.task.body}</p>
        </div>
        <div class="d-flex align-items-center mx-5">
          <button 
          type='button'
          class=" mx-1 btn btn-outline-info btn-sm"
          data-bs-toggle="modal" data-bs-target="#exampleMod"
          onClick={handleShow}
          >
            Edit
          </button>
          <button 
          class=" mx-1 btn btn-outline-danger btn-sm"
          onClick={() => {}}
          >
            Delete
          </button>
        </div>
      </div>    

      <Modal show={show} onHide={handleClose} aria-labelledby="contained-modal-title-vcenter" centered>
        <Form onSubmit={onFormSubmit}>
          <Modal.Header closeButton>
            <Modal.Title> 
              <Form.Control type="input" placeholder={"Enter New Title"} defaultValue={props.task.title} onChange={onValue} value={value.title} /> 
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Control as="textarea" rows={8} placeholder={"Enter New Comment"} defaultValue={props.task.body} /> 
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary btn-sm" onClick={handleClose}> 
              Close
            </Button>
            <Button variant="success btn-sm" type="submit" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

    </div>
  )
}

export default TaskItem
