import * as type from "../actions/ActionTypes";

const filterReducer = (state = {filter: "all"}, action) => {
    switch(action.type) {
        case type.SET_ALL:
            return {filter: "all"};
        case type.SET_OPEN:
            return {filter: "open"};
        case type.SET_IN_PROGRESS:
            return {filter: "in_progress"};
        case type.SET_DONE:
            return {filter: "closed"};
        default:
            return state;
    }
}

export default filterReducer;
