import * as type from "../actions/ActionTypes";

async function getAllTasks() {
    let list = [];
    await fetch("http://localhost:4000/getAllTasks", {
      method: "GET", 
        headers: {
          "Authorization": "Bearer " + localStorage.getItem("accessToken")
        }
      }).then((response) => {
        return response.json();
      }).then((data) => {
        for(let i = 0; i < data.length; i++) {
            // console.log(i);
            let tmpObj = {
                id: data[i].id,
                title: data[i].title,
                body: data[i].body,
                state: data[i].state,
                created_at: data[i].created_at
            };
            list.push(tmpObj);
        }

    }) 
    return list;
}

const taskReducer = (state = getAllTasks(), action) => {
    switch(action.type) {
        case type.GET_ALL_TASK:
            return getAllTasks();
        default:
            return state;
    }
}

export default taskReducer;
