import * as type from "../actions/ActionTypes";

const taskReducer = (state = [], action) => {
    switch(action.type) {
        case type.GET_ALL_TASK:
            async function initialTask(){   
                let list = [];
                await fetch("http://localhost:4000/getAllTasks", {
                method: "GET", 
                    headers: {
                    "Authorization": "Bearer " + localStorage.getItem("accessToken")
                    }
                }).then((response) => {
                    return response.json();
                }).then((data) => {
                    // console.log(data);
                    for(let i = 0; i < data.length; i++) {
                        let tmpObj = {
                            id: data[i].id,
                            title: data[i].title,
                            body: data[i].body,
                            state: data[i].state,
                            repo: data[i].repository.name,
                            owner: data[i].repository.owner.login,
                            created_at: data[i].created_at,
                            issue_number: data[i].number
                        };
                        // console.log(list);
                        list.push(tmpObj);
                    }           
                }) 
                // state = list;
                console.log(list);
                // console.log(state);

                return list;
            }

            let taskList = [];

            //initialTask();
            console.log(initialTask().then((res) => {taskList = res; console.log("res" + res.length); return res;}));
            console.log(taskList);
            return state;
        default:
            return state;
    }
}

export default taskReducer;
